import React from "react";
import { useEffect } from "react";
import { useState } from "react";

function PhotoPage() {
  const [photos, setPhotos] = useState([]);

  useEffect(() => {
    fetch("https://picsum.photos/v2/list")
      .then((res) => res.json())
      .then((res) => {
        setPhotos(res);
      })
      .catch((err) => console.log(err));
  }, []);

  return (
    <div style={{ padding: "4% 4px", width: "100vw" }}>
      <div style={{ display: "flex", justifyContent: "space-between" }}>
        <span>Everyone's photos</span>
        <span style={{ color: "#1c5591" }}>View all 2,724,700</span>
      </div>
      <div style={{}}>
        <div
          style={{
            display: "grid",
            gridGap: "4px",
            // gridAutoFlow: "column",
            // grid: "auto / auto auto",
            // gridAutoRows: "auto",
            gridTemplateRows: "repeat(auto-fill, 150px)",
            gridTemplateRows: "150px",
            // justifyContent: "space-between",
            // flexWrap: "wrap",
            // gap: "4px",
            // flexGrow: "4px",
          }}
        >
          {photos.map((photo) => (
            <div>
              <img
                src={photo.download_url}
                alt={photo.author}
                style={{
                  height: "150px",
                }}
              />
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default PhotoPage;
