import React from "react";
import { useEffect } from "react";
import { useLocation, useParams } from "react-router-dom";
import { getBookingsForWeek, getTime } from "../../02/bookingServices";
import { useBooking } from "../BookingContext";

function ScheduleDetailPage() {
  const { schedule, setSchedule } = useBooking();
  const { pathname } = useLocation();
  const week = pathname.split("/")[2];
  const { roomId } = useParams();

  let weekNo;
  const getWeekNo = () => {
    switch (week) {
      case "thisweek":
        weekNo = 1;
        break;
      case "nextweek":
        weekNo = 2;
        break;
      default:
        return "";
        break;
    }
  };

  useEffect(() => {
    setSchedule(getBookingsForWeek(roomId, weekNo));
  }, [week]);

  return (
    <div>
      {" "}
      <div className="left-schedule ">
        {schedule.map((booking) => (
          <div className="left-today-schedule">
            <p className="text-16 opacity-50 font-400">
              {getTime(booking.startTime, booking.endTime)}
            </p>
            <p className="text-20 font-400">{booking.title}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default ScheduleDetailPage;
